import './App.css';
import * as React from "react";
import { Admin, Resource } from 'react-admin';
import jsonServerProvider from 'ra-data-json-server';
import { stringify } from 'query-string';
import { fetchUtils } from 'ra-core';

import {
    TournamentList,
} from './pages/Tournaments';
import {
    SeasonList,
} from './pages/Seasons';

const dataProvider = jsonServerProvider('http://localhost:8080/api');


dataProvider.getList = (resource, params) => {
    const { page, perPage } = params.pagination;
    const { field, order } = params.sort;
    const query = {
        sort: JSON.stringify([field, order]),
        range: JSON.stringify([(page - 1) * perPage, page * perPage - 1]),
        filter: JSON.stringify(params.filter),
    };
    const url = `http://localhost:8080/api/${resource}?${stringify(query)}`;

    return fetchUtils.fetchJson(url).then(({ headers, json }) => {
        let jsonData;
        switch(resource) {
            case "tournaments": {
                jsonData = json.tournaments;
                break;
            }
            case "seasons": {
                jsonData = json.seasons;
                break;
            }
            default: {
                break;
            }
        }

        const res = {
            data: jsonData.map(resource => ({...resource, id: resource.alias})),
            total: jsonData.length
        };
        console.log(res);
        return res
    });
};


const App = () => {
    return (
        <Admin dataProvider={dataProvider}>
            <Resource name="tournaments" list={TournamentList}/>
            <Resource name="seasons" list={SeasonList}/>
        </Admin>
    );
};

export default App;
